var input = "";

Console.WriteLine("Start packet in input: {0}", GetStartPosition(4, input));
Console.WriteLine("Start message in input: {0}", GetStartPosition(14, input));

int GetStartPosition(int uniqueCharacters, string input)
{
    for (var i = uniqueCharacters; i < input.Length - uniqueCharacters; i++)
    {
        var sub = input.Substring(i - uniqueCharacters, uniqueCharacters).ToCharArray().Distinct();
        
        if (sub.Count() == uniqueCharacters)
        {
            return i;
        }
    }

    throw new Exception("Could not find unique characters");
}
